mapper.select:
select id, gmt_create, gmt_modified, name, goods_sn, short_name, description, brief, pic_url, detail, status, share_url, gallery, goods_category_id, brand_id, is_deleted, weight, volume, special_freight_id, is_special
from goods
where is_deleted = 0
order by ${key} ${order}
limit  ${pageNum * rowNum - rowNum},${pageNum * rowNum}
